import { useState, useEffect } from "react";

export default function useDeounce(value) {
  const [debouncedValue, setDebouncedValue] = useState(value);
  useEffect(() => {
    let timer = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);
  }, [value, delay]);
  return null;
}
